let minTs = toscalar(materialized_view("dailyAggJobEvents")|summarize minTs = min(["time"]));
let allUsers = materialized_view("dailyAggJobEvents")
            |where ["time"] between (minTs ..now()-7d )
            |distinct executingPrincipalId, executingPrincipalType;
let yesterdayUsers = materialized_view("dailyAggJobEvents")
            |where ["time"] between (now()-7d..now() )
            |distinct executingPrincipalId, executingPrincipalType;            
allUsers
| join kind=rightanti yesterdayUsers on executingPrincipalId, executingPrincipalType
|summarize userCount = count() by executingPrincipalId, executingPrincipalType

let span = 1d;
let minTs = now()-1d;
let maxTs = now();
materialized_view("dailyAggStorageEvents")
| where ["time"] between (minTs..maxTs) and isnotempty(type)
| make-series tsCount = count() default=int(null) on ["time"] from minTs to maxTs step span by type
| extend tsCount = series_fill_linear(tsCount) // replace missing null values with interpolated data
| extend (anamolies, score,baseline) = series_decompose_anomalies(tsCount,1.5,-1,"linefit")
| where isnotnull(anamolies) and array_index_of( anamolies,1) !=-1
| summarize count = count() by type
