jobEventsExpanded
| where jobStatus == 'Completed' and jobEndTimeUtc >= ago(45d)
| extend jobDurationSeconds = jobDurationSeconds
| project jobEndTimeUtc, itemName, jobType, jobDurationSeconds
| sort by jobEndTimeUtc desc


jobEventsExpanded
| where jobStatus == 'Completed' and jobEndTimeUtc >= ago(45d)
| extend jobDurationMin = jobDurationSeconds / 60
| project jobEndTimeUtc, itemName, jobType, jobDurationMin
| sort by jobEndTimeUtc desc

jobEventsExpanded
| where jobEndTimeUtc >= ago(90d)
| extend executionDurationMins = jobDurationSeconds / 60
| summarize avgExecutionDurationMins=avg(executionDurationMins) by bin(jobEndTimeUtc, 1d), itemName
| project jobEndTimeUtc, itemName, avgExecutionDurationMins
| order by jobEndTimeUtc asc
| render timechart



